hudson.tasks.Shell.command:
#!/bin/bash
function download_script() { local download_file=${1?} . /etc/profile [ -n "$DOWNLOAD_TAG_NAME" ] || export DOWNLOAD_TAG_NAME="tag_v2" working_dir="/var/lib/jenkins/code/devops_public" bash_script="${working_dir}/${download_file}" dir_name=$(dirname "$bash_script")
export DOWNLOAD_PREFIX="https://raw.githubusercontent.com/DennyZhang/devops_public/${DOWNLOAD_TAG_NAME}" if [ ! -f "$bash_script" ]; then mkdir -p "$dir_name" wget -O "$bash_script" "${DOWNLOAD_PREFIX}/jenkins_scripts/${download_file}" chmod 755 "$bash_script" fi } ################################################################################ download_file="report/digitalocean_cost_slack_report.sh" download_script "$download_file"
export DIGITALOCEAN_TOKEN=YOUR_DIGITALOCEAN_TOKEN export SLACK_TOKEN=YOUR_SLACK_TOKEN
bash -e "/var/lib/jenkins/code/devops_public/$download_file"
